def get_all_merchants():
    """Return list of all merchant summaries"""
    return [
        {
            "id": key,
            "name": merchant["name"],
            "business_type": merchant["business_type"],
            "location": merchant["location"],
            "problem_statement": merchant["problem_statement"]
        }
        for key, merchant in MERCHANTS.items()
    ]


def get_merchant_data(merchant_id):
    """Return full merchant data including 12 months of KPIs"""
    if merchant_id not in MERCHANTS:
        return None
    return MERCHANTS[merchant_id]
